import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function CardPicture(props) {
  let masterData = props.input;
  const navigate = useNavigate(); // Hook for navigating programmatically

  const handleVisitTaskClick = (taskElementId) => {
    navigate(taskElementId); // Navigate to the task's route
    scrollToTask(taskElementId); // Scroll to the task's position
  };

  const scrollToTask = (taskId) => {
    const taskElement = document.getElementById(taskId);
    if (taskElement) {
      const topOffset = taskElement.getBoundingClientRect().top + window.pageYOffset;
      window.scrollTo({ top: topOffset, behavior: 'smooth' });
    }
  };

  return (
    <>
      {masterData.map((obj) => {
        const taskElementId = obj.urlTask; // Use task's URL as element ID

        return (
          <div key={taskElementId} className='col-sm-12 col-md-6 col-lg-3 mt-4'>
            <div className="card text-center">
              <img src={obj.urlImg} className="card-img-top rounded mx-auto d-block" alt="SimpleImage" />
              <div className="card-body text-center">
                <h5 className="card-title">{obj.title}</h5>
                <h5 className="card-text">{obj.text}</h5>
                {/* Use the task's URL as the element ID */}
                <button onClick={() => handleVisitTaskClick(taskElementId)} className="btn btn-primary">Visit Task</button>
              </div>
            </div>
          </div>
        );
      })}
    </>
  );
}
