import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function CardPicture(props) {
  let masterData = props.input;
  const navigate = useNavigate(); // Hook for navigating programmatically

  return (
    <>
      {masterData.map((obj) => {
        const handleVisitTaskClick = () => {
          // Navigate to the task's route when the button is clicked
          navigate(obj.urlTask);
        };

        return (
          <div key={obj.urlTask} className='col-sm-12 col-md-6 col-lg-3 mt-4'>
            <div className="card text-center">
              <img src={obj.urlImg} className="card-img-top rounded mx-auto d-block" alt="SimpleImage" />
              <div className="card-body text-center">
                <h5 className="card-title">{obj.title}</h5>
                <h5 className="card-text">{obj.text}</h5>
                {/* Use a function to handle button click */}
                <button onClick={handleVisitTaskClick} className="btn btn-primary">Visit Task</button>
              </div>
            </div>
          </div>
        );
      })}
    </>
  );
}
