import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
export default function Tododisplay({ item, todoItems, setTodoItems }) {
  const handleDelete = () => {
    setTodoItems(todoItems.filter((obj) => obj.id !== item.id));
  };

  const handleToggleCompletion = () => {
    setTodoItems(
      todoItems.map((obj) => {
        if (obj.id === item.id) {
          return { ...obj, completed: !obj.completed };
        }
        return obj;
      })
    );
  };

  return (
    <div
      className={`container-fluid row mt-3 ${
        item.completed ? "bg-light text-muted" : "bg-white"
      }`}
    >
      <div className="col-sm-1">
        <input
          type="checkbox"
          checked={item.completed}
          onChange={handleToggleCompletion}
        />
      </div>
      <div
        className={`col-sm-9 ${item.completed ? "text-muted" : "text-dark"}`}
        onClick={handleToggleCompletion}
      >
        <h5>{item.name}</h5>
      </div>
      <div className="col-sm-2">
        <button className="btn btn-danger" onClick={handleDelete}>
          delete
        </button>
      </div>
    </div>
  );
}
