import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";

const Tododisplay = ({ item, todoItems, setTodoItems, handleToggleCompletion, handleDelete }) => {
  return (
    <div className={`container-fluid row mt-3 ${item.completed ? "bg-light text-muted" : "bg-white"}`}>
      <div className="col-sm-1">
        <input
          type="checkbox"
          checked={item.completed}
          onChange={() => handleToggleCompletion(item.id)}
        />
      </div>
      <div className={`col-sm-9 ${item.completed ? "text-muted" : "text-dark"}`} onClick={() => handleToggleCompletion(item.id)}>
        <h5>{item.name}</h5>
      </div>
      <div className="col-sm-2">
        <button className="btn btn-danger" onClick={() => handleDelete(item.id)}>
          delete
        </button>
      </div>
    </div>
  );
};

export default Tododisplay;
