import React from "react";
import Tododisplay from "./Tododisplay";

export default function Todolist({ todoItems, setTodoItems, hasLoaded }) {
  const handleToggleCompletion = (itemId) => {
    setTodoItems(
      todoItems.map((obj) => {
        if (obj.id === itemId) {
          return { ...obj, completed: !obj.completed };
        }
        return obj;
      })
    );
  };

  const handleDelete = (itemId) => {
    setTodoItems(todoItems.filter((obj) => obj.id !== itemId));
  };

  return (
    <div>
      {hasLoaded &&
        (todoItems.length > 0 ? (
          todoItems.map((item) => (
            <Tododisplay
              key={item.id}
              item={item}
              handleToggleCompletion={() => handleToggleCompletion(item.id)}
              handleDelete={() => handleDelete(item.id)}
            />
          ))
        ) : (
          <div className="text-center mt-3">No tasks to do today.</div>
        ))}
    </div>
  );
}
