import React, { useRef } from 'react';
import { useNavigate } from 'react-router-dom';

export default function CardPicture(props) {
  const masterData = props.input;
  const navigate = useNavigate();
  const scrollRef = useRef(null); // Create a ref for scrolling

  const handleVisitTask = (urlTask) => {
    navigate(urlTask); // Navigate to the specific task
    scrollRef.current.scrollIntoView({ behavior: 'smooth' }); // Scroll to the ref
  };

  return (
    <>
      {masterData.map((obj) => {
        return (
          <div key={obj.urlTask} className='col-sm-12 col-md-6 mt-4'>
            <div className="card text-center">
              <img src={obj.urlImg} className="card-img-top rounded mx-auto d-block" alt="SimpleImage" />
              <div className="card-body text-center">
                <h5 className="card-title">{obj.title}</h5>
                <h5 className="card-text">{obj.text}</h5>
                {/* Use onClick to navigate to the specific task and scroll */}
                <button onClick={() => handleVisitTask(obj.urlTask)} className="btn btn-primary">Visit Task</button>
              </div>
            </div>
          </div>
        );
      })}

      {/* This invisible div will be used for scrolling */}
      <div ref={scrollRef} style={{ position: 'absolute', top: 0, left: 0 }} />
    </>
  );
}
