// CardPicture.js
import React from 'react';
import { Link, Routes, Route, Outlet, useParams } from 'react-router-dom';
import FirstComponent from '../Component_Day13/FirstComponent';
import Profile1 from '../Component_Day13/Profile1';
import Props from '../Component_Day13/Props';
import Second from '../Component_Day13/Second';

export default function CardPicture(props) {
  let masterData = props.input;

  return (
    <>
      {masterData.map((obj) => {
        return (
          <div key={obj.urlTask} className='col-sm-12 col-md-6 col-lg-3 mt-4'>
            <div className="card text-center">
              <img src={obj.urlImg} className="card-img-top rounded mx-auto d-block" alt="SimpleImage" />
              <div className="card-body text-center">
                <h5 className="card-title">{obj.title}</h5>
                <h5 className="card-text">{obj.text}</h5>
                {/* Use Link to navigate to the specific task */}
                <Link to={obj.urlTask} className="btn btn-primary">Visit Task</Link>
              </div>
            </div>
          </div>
        )
      })}

      {/* Nested routing for individual task components */}
      <Routes>
        <Route path="/FirstComponent" element={<FirstComponent />} />
        <Route path="/Profile1" element={<Profile1 />} />
        <Route path="/Props" element={<Props />} />
        <Route path="/Second" element={<Second />} />
        <Route index element={<Outlet />} />
      </Routes>
    </>
  )
}
